import React, { useEffect, useRef, useState } from "react";

export const MusicPlayer = (selectedMusic) => {
  const audioRef = useRef();
  const [isPlaying, setIsPlaying] = useState(true);
  const [start] = useState(0);
  const [end, setEnd] = useState(0);
  const [addedToWatchlist, setAddedToWatchlist] = useState(false);

  debugger;
  const { thumbnail, title, artist, audio_url } = selectedMusic;
  const artistList = artist && artist.map((item) => item.name).join(" & ");

  const playPauseAudio = () => {
    setIsPlaying(!isPlaying);
  };

  const getTime = (duration) => {
    console.log("duration", duration);
    const endTime = Math.ceil(duration);
    let min = Math.floor(endTime / 60);
    let sec = endTime % 60;
    return `${min}:${sec}`;
  };

  useEffect(() => {
    debugger
    if (audioRef.current) {
      const endTime = getTime(audioRef.current.duration);
      // if(typeof endTime)
      if(endTime != "NaN:NaN"){
         console.log("endTime", endTime);
      setEnd(endTime);
      }

      if (isPlaying) {
        audioRef.current.play();
        console.log("duration", audioRef.current.duration);
      } else {
        audioRef.current.pause();
      }
    }
  }, [isPlaying, audioRef]);

  if (!title) {
    return <></>;
  }


  return (
    <section className="music-player">
      <img src={thumbnail} alt={title} height="50" width="50" />
        <>
          <div className="song-info">
            <div>{title}</div>
            <div title={artistList} className="artist-list">
              {artistList}
            </div>
          </div>
          <button onClick={playPauseAudio} id="play" className="play-pause">
            {isPlaying ? <i class="fa-solid fa-play"></i> : <i class="fa-solid fa-pause"></i>}
          </button>
          <div>{start}</div>
          <div>{end=='NaN'?0:end}</div>
          <audio src={audio_url} ref={audioRef} />
          <div className="heart-icon">
            {addedToWatchlist ? <i class="fa-solid fa-heart"></i>:  <i class="fa-regular fa-heart"></i>}
          </div>
        </>
    </section>
  );
};

export default MusicPlayer;
